  def littleL(a: Array[Byte], i: Int) =
    ((a(i + 0) & 0xFF).toLong      ) |
    ((a(i + 1) & 0xFF).toLong <<  8) |
    ((a(i + 2) & 0xFF).toLong << 16) |
    ((a(i + 3) & 0xFF).toLong << 24) |
    ((a(i + 4) & 0xFF).toLong << 32) |
    ((a(i + 5) & 0xFF).toLong << 40) |
    ((a(i + 6) & 0xFF).toLong << 48) |
    ((a(i + 7) & 0xFF).toLong << 56)

  def littleI(a: Array[Byte], i: Int) =
    ((a(i + 0) & 0xFF)      ) |
    ((a(i + 1) & 0xFF) <<  8) |
    ((a(i + 2) & 0xFF) << 16) |
    ((a(i + 3) & 0xFF) << 24)

  def test_xx = {
    (dataToHash zip xxHashCanonicalZeroSeed).forall{ case (a, (i, l)) =>
      (XX.hash32(a, 0) == i).tap(x => if (!x) println(f"$i%x != ${XX.hash32(a,0)}%x on ${a.mkString(", ")}")) && 
      (XX.hash64(a, 0) == l).tap(x => if (!x) println(f"$l%s != ${XX.hash64(a,0)}%x on ${a.mkString(", ")}"))        
    }
  }

  def test_xx_bb = {
    (dataToHash zip xxHashCanonicalZeroSeed).forall{ case (a, (i,l)) =>
      (XX.hash32(ByteBuffer.wrap(a), 0) == i).tap(x => if (!x) println(f"Failed XX.hash32 on $i%x (length ${a.length}")) &&
      ((new XxHash32(0)).result(ByteBuffer wrap a) == i).tap(x => if (!x) println(f"Failed XxHash32 on $i%x (length ${a.length})")) &&
      (XX.hash64(ByteBuffer.wrap(a), 0) == l).tap(x => if (!x) println(f"Failed XX.hash64 on $l%x (length ${a.length}")) &&
      ((new XxHash64(0)).result(ByteBuffer wrap a) == l).tap(x => if (!x) println(f"Failed XxHash64 on $l%x (length ${a.length}"))
    }
  }

  def test_xx_inc = {
    (dataToHash zip xxHashCanonicalZeroSeed).forall{ case (a,(i,l)) =>
      val x32 = {
        val xx32 = new XxHash32
        var j = 0
        while (j+16 <= a.length) {
          xx32.appendIx4(littleI(a,j), littleI(a,j+4), littleI(a,j+8), littleI(a,j+12))
          j += 16
        }
        xx32.counting(a.length - j)
        while (j+4 <= a.length) {
          xx32 trailing littleI(a,j)
          j += 4
        }
        while (j < a.length) {
          xx32 trailing a(j)
          j += 1
        }
        xx32.complete()
      }
      val x64 = {
        val xx64 = new XxHash64
        var k = 0
        while (k+32 <= a.length) {
          xx64.appendLx4(littleL(a,k), littleL(a,k+8), littleL(a,k+16), littleL(a,k+24))
          k += 32
        }
        xx64.counting(a.length - k)
        while (k+8 <= a.length) {
          xx64 trailing littleL(a,k)
          k += 8
        }
        if (k+4 <= a.length) {
          xx64 trailing littleI(a,k)
          k += 4
        }
        while (k < a.length) {
          xx64 trailing a(k)
          k += 1
        }
        xx64.complete()
      }
      (x32 == i).tap(x => if (!x) println(f"Failed incremental XxHash32 on $i%x (length ${a.length}")) &&
      (x64 == l).tap(x => if (!x) println(f"Failed incremental XxHash64 on $l%x (length ${a.length}"))
    }
  }

  def test_consistency_all = {
    val xx32 = new XxHash32
    val cs32 = new SimpleSum32
    val mh32 = new Murmur32
    val xx64 = new XxHash64
    val cs64 = new SimpleSum64
    val mh128 = new Murmur128
    def pairsame[A](v: Vector[A], what: => String) = v.zipWithIndex.sliding(2).forall{ x =>
      val ans = x(0)._1 == x(1)._1
      if (!ans) println(f"$what\nMismatch on ${x(0)._2} vs ${x(1)._2}: ${x(0)._1} != ${x(1)._1}")
      ans
    }
    (1 to 500).forall{ i =>
      val a = Array.fill(i)(util.Random.nextInt.toByte)
      val b = a.take(util.Random.nextInt(a.length))
      val c = a.drop(b.length)
      pairsame(Vector(
        XX.hash32(ByteBuffer wrap a),
        xx32.hash32(ByteBuffer wrap a),
        xx32.hash32(ByteBuffer wrap a),
        xx32.begin(0).append(ByteBuffer wrap b).result(ByteBuffer wrap c),
        xx32.begin(0).result(ByteBuffer wrap a),
        xx32.hash32(ByteBuffer wrap a)
      ), f"xx32 $i ${b.length}\n${a.toVector}\n") &&
      pairsame(Vector(
        mh32.hash32(ByteBuffer wrap a),
        mh32.begin(0).append(ByteBuffer wrap b).result(ByteBuffer wrap c),
        mh32.begin(0).result(ByteBuffer wrap a),
        mh32.hash32(ByteBuffer wrap a)
      ), f"mh32 $i ${b.length}\n${a.toVector}\n") &&
      pairsame(Vector(
        cs32.hash32(ByteBuffer wrap a),
        cs32.begin(0).append(ByteBuffer wrap b).result(ByteBuffer wrap c),
        cs32.begin(0).result(ByteBuffer wrap a),
        cs32.hash32(ByteBuffer wrap a)
      ), f"cs32 $i ${b.length}\n${a.toVector}\n") &&
      pairsame(Vector(
        XX.hash64(ByteBuffer wrap a),
        xx64.hash64(ByteBuffer wrap a),
        xx64.begin(0).append(ByteBuffer wrap b).result(ByteBuffer wrap c),
        xx64.begin(0).result(ByteBuffer wrap a),
        xx64.hash64(ByteBuffer wrap a)
      ), f"xx64 $i ${b.length}") &&
      pairsame(Vector(
        cs64.hash64(ByteBuffer wrap a),
        cs64.begin(0).append(ByteBuffer wrap b).result(ByteBuffer wrap c),
        cs64.begin(0).result(ByteBuffer wrap a),
        cs64.hash64(ByteBuffer wrap a)
      ), f"cs64 $i ${b.length}\n${a.toVector}\n") &&
      pairsame(Vector(
        mh128.hash128(ByteBuffer wrap a).toVector,
        mh128.begin(0).append(ByteBuffer wrap b).results(ByteBuffer wrap c).toVector,
        mh128.begin(0).results(ByteBuffer wrap a).toVector,
        mh128.hash128(ByteBuffer wrap a).toVector
      ), f"mh128 $i ${b.length}\n${a.toVector}\n")
    }
  }

  def test_murmur32 = {
    val h = new Murmur32
    (1 to 100).forall{ i => 
      val a = Array.fill(i)(util.Random.nextInt.toByte)
      scala.util.hashing.MurmurHash3.bytesHash(a, 0) == h.hash32(ByteBuffer wrap a)
    }
  }

  def test_murmur128_x64 = {
    val h = new Murmur128
    val expected = Vector(0xcd99481f9ee902c9L, 0x695da1a38987b6e7L)  // Verified on http://murmurhash.shorelabs.com/
    h.hash128(ByteBuffer wrap "The quick brown fox jumps over the lazy dog.".getBytes).toVector == expected
  }

  def test_simplesum32 = {
    val h = new SimpleSum32
    (1 to 1000).forall{ i =>
      val a = Array.fill(i)(util.Random.nextInt.toByte)
      val bb = ByteBuffer wrap a
      val sum = {
        var j, s = 0
        while (j+3 < a.length) {
          s += littleI(a, j)
          j += 4
        }
        if (j < a.length) {
          val k = j
          while (j < a.length) {
            s += ((a(j) & 0xFF) << (j-k)*8)
            j += 1
          }
        }
        s
      }
      val ans = h hash32 bb
      sum == ans
    }
  }

  def test_simplesum64 = {
    val h = new SimpleSum64
    (1 to 1000).forall{ i =>
      val a = Array.fill(i)(util.Random.nextInt.toByte)
      val bb = ByteBuffer wrap a
      val sum = {
        var j = 0
        var s = 0L
        while (j+7 < a.length) {
          s += littleL(a, j)
          j += 8
        }
        if (j < a.length) {
          val k = j
          while (j < a.length) {
            s += ((a(j) & 0xFFL) << (j-k)*8)
            j += 1
          }       
        }
        s
      }
      val ans = h hash64 bb
      if (sum != ans) println(f"$i $sum $ans ${a.toVector}")
      sum == ans
    }
  }

